import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CountryList = () => {
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/countries/available-countries');
        setCountries(response.data);
        setLoading(false);
      } catch (error) {
        setError('Error al obtener la lista de países');
        setLoading(false);
      }
    };

    fetchCountries();
  }, []);

  if (loading) return <div className="text-center text-xl">Cargando países...</div>;
  if (error) return <div className="text-center text-xl text-red-500">{error}</div>;

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <h2 className="text-3xl font-bold text-center mb-6">Lista de Países</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-6">
        {countries.map((country) => (
          <div
            key={country.countryCode}
            className="bg-white p-4 rounded-lg shadow-md hover:shadow-xl transition duration-300"
          >
            <Link to={`/country/${country.countryCode}`} className="block text-center">
              <div className="w-full h-32 object-contain bg-gray-200 rounded-lg mb-4">
                <img
                  src={country.flag}
                  alt={country.name}
                  className="w-full h-full object-contain rounded-lg"
                />
              </div>
              <h3 className="text-xl font-semibold">{country.name}</h3>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CountryList;
