import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CountryInfo = ({ countryCode }) => {
  const [countryInfo, setCountryInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCountryInfo = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/countries/country-info/${countryCode}`);
        setCountryInfo(response.data);
        setLoading(false);
      } catch (error) {
        setError('Error al obtener la información del país');
        setLoading(false);
      }
    };

    if (countryCode) {
      fetchCountryInfo();
    }
  }, [countryCode]);

  if (loading) return <div>Cargando información...</div>;
  if (error) return <div>{error}</div>;
  if (!countryInfo) return null;

  return (
    <div>
      <h2>Información de {countryCode}</h2>
      <p><strong>Países vecinos:</strong> {countryInfo.borders.join(', ')}</p>
      <p><strong>Población:</strong> {countryInfo.populationData.map((pop) => `${pop.year}: ${pop.population}`).join(', ')}</p>
      <img src={countryInfo.flagUrl} alt={`Bandera de ${countryCode}`} style={{ width: '100px' }} />
    </div>
  );
};

export default CountryInfo;
