import * as React from 'react';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';

const CountryList = () => {
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/countries/available-countries');
        setCountries(response.data);
        setLoading(false);
      } catch (error) {
        setError('Error al obtener la lista de países');
        setLoading(false);
      }
    };

    fetchCountries();
  }, []);

  if (loading) return <div className="text-center text-xl">Cargando países...</div>;
  if (error) return <div className="text-center text-xl text-red-500">{error}</div>;

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <h2 className="text-3xl font-bold text-center mb-6">Lista de Países</h2>
      <div className="w-full max-w-6xl mx-auto">
        <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
          {countries.map((country) => (
            <React.Fragment key={country.countryCode}>
              <ListItem alignItems="flex-start">
                <ListItemAvatar>
                  <Avatar alt={country.name} src={country.flag} />
                </ListItemAvatar>
                <ListItemText
                  primary={<Link to={`/country/${country.countryCode}`} className="text-blue-500 hover:underline">{country.name}</Link>}
                />
              </ListItem>
              <Divider variant="inset" component="li" />
            </React.Fragment>
          ))}
        </List>
      </div>
    </div>
  );
};

export default CountryList;
