import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CountryList = ({ onSelectCountry }) => {
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/countries/available-countries');
        setCountries(response.data);
        setLoading(false);
      } catch (error) {
        setError('Error al obtener la lista de países');
        setLoading(false);
      }
    };

    fetchCountries();
  }, []);

  if (loading) return <div>Cargando países...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Lista de países</h2>
      <ul>
        {countries.map((country) => (
          <li key={country.countryCode} onClick={() => onSelectCountry(country.countryCode)}>
            {country.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CountryList;
