import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// Registrar los componentes de Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// Datos de población de los países (simulados)
const populationData = {
  error: false,
  msg: 'all countries and population 1961 - 2018',
  data: [
    { country: 'Argentina', code: 'ARG', iso3: 'ARG', populationCounts: Array.from({ length: 59 }, (_, i) => ({ year: 1961 + i, value: Math.floor(Math.random() * 10000000) + 20000000 })) },
    { country: 'Brazil', code: 'BRA', iso3: 'BRA', populationCounts: Array.from({ length: 59 }, (_, i) => ({ year: 1961 + i, value: Math.floor(Math.random() * 20000000) + 30000000 })) },
    // Agrega más países aquí
  ]
};

// Función para obtener la población de un país específico
function getPopulation(countryName) {
  const country = populationData.data.find(entry => entry.country === countryName);
  
  if (country) {
    return country.populationCounts;  // Devuelve el arreglo de población de 59 años
  } else {
    return `No se encontró el país: ${countryName}`;
  }
}

const CountryInfo = () => {
  const { countryCode } = useParams();
  const [countryInfo, setCountryInfo] = useState(null);
  const [borders, setBorders] = useState([]); // Estado para las fronteras
  const [flag, setFlag] = useState(''); // Estado para la bandera
  const [populationChartData, setPopulationChartData] = useState({ labels: [], datasets: [] });

  useEffect(() => {
    const fetchCountryInfo = async () => {
      try {
        // Obtener la información del país
        const response = await fetch(`https://restcountries.com/v3.1/alpha/${countryCode}`);
        const data = await response.json();
        setCountryInfo(data[0]);

        // Obtener las banderas desde el endpoint de flags
        const flagResponse = await fetch('https://countriesnow.space/api/v0.1/countries/flag/images');
        const flagData = await flagResponse.json();

        // Buscar la URL de la bandera correspondiente al país
        const countryFlag = flagData.data.find(country => country.iso2 === countryCode)?.flag;
        setFlag(countryFlag);

        // Obtener los nombres de los países vecinos
        const borderNames = await Promise.all(
          data[0].borders.map(async (borderCode) => {
            const res = await fetch(`https://restcountries.com/v3.1/alpha/${borderCode}`);
            const borderData = await res.json();
            return {
              name: borderData[0].name.common,
              code: borderData[0].cca2, // Código de país en formato de dos letras
            };
          })
        );
        setBorders(borderNames); // Guardar los nombres y códigos de las fronteras

        // Obtener los datos de población desde el objeto populationData
        const countryPopulation = getPopulation(data[0].name.common);
        
        if (Array.isArray(countryPopulation)) {
          const years = countryPopulation.map(entry => entry.year);
          const populations = countryPopulation.map(entry => entry.value);

          // Preparar los datos para el gráfico
          setPopulationChartData({
            labels: years,
            datasets: [
              {
                label: `${data[0].name.common} Population Over Time`,
                data: populations,
                fill: false,
                borderColor: 'rgb(75, 192, 192)',
                tension: 0.1,
              },
            ],
          });
        }
        
      } catch (error) {
        console.error('Error al obtener la información del país:', error);
      }
    };

    fetchCountryInfo();
  }, [countryCode]);

  if (!countryInfo) return <p>Cargando información...</p>;

  const {
    name,
  } = countryInfo;

  return (
    <div>
      <h2>{name.common}</h2>
      {flag ? (
        <img src={flag} alt={`Bandera de ${name.common}`} width="100" />
      ) : (
        <p>Bandera no disponible</p>
      )}
      <p><strong>Fronteras:</strong></p>
      {borders.length > 0 ? (
        <ul>
          {borders.map((border, index) => (
            <li key={index}>
              <Link to={`/country/${border.code}`} className="text-blue-600 hover:underline">
                {border.name}
              </Link>
            </li>
          ))}
        </ul>
      ) : (
        <p>No hay fronteras disponibles.</p>
      )}

      {/* Mostrar el gráfico de población */}
      <div>
        <h3>Gráfico de población a lo largo del tiempo</h3>
        {populationChartData.labels.length > 0 ? (
          <Line data={populationChartData} />
        ) : (
          <p>No hay datos de población disponibles para este país.</p>
        )}
      </div>

      {/* Botón para volver a la lista de países */}
      <div className="mt-4">
        <Link to="/" className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
          Volver a la lista de países
        </Link>
      </div>
    </div>
  );
};

export default CountryInfo;
