import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const CountryInfo = () => {
  const { countryCode } = useParams();
  const [countryInfo, setCountryInfo] = useState(null);
  const [borders, setBorders] = useState([]); // Estado para las fronteras
  const [flag, setFlag] = useState(''); // Estado para la bandera

  useEffect(() => {
    const fetchCountryInfo = async () => {
      try {
        // Obtener la información del país
        const response = await fetch(`https://restcountries.com/v3.1/alpha/${countryCode}`);
        const data = await response.json();
        setCountryInfo(data[0]);

        // Obtener las banderas desde el endpoint de flags
        const flagResponse = await fetch('https://countriesnow.space/api/v0.1/countries/flag/images');
        const flagData = await flagResponse.json();

        // Buscar la URL de la bandera correspondiente al país
        const countryFlag = flagData.data.find(country => country.iso2 === countryCode)?.flag;
        setFlag(countryFlag);

        // Obtener los nombres de los países vecinos
        const borderNames = await Promise.all(
          data[0].borders.map(async (borderCode) => {
            const res = await fetch(`https://restcountries.com/v3.1/alpha/${borderCode}`);
            const borderData = await res.json();
            return borderData[0].name.common;
          })
        );

        setBorders(borderNames); // Guardar los nombres de las fronteras
      } catch (error) {
        console.error('Error al obtener la información del país:', error);
      }
    };

    fetchCountryInfo();
  }, [countryCode]);

  if (!countryInfo) return <p>Cargando información...</p>;

  const {
    name,
    population,
  } = countryInfo;

  return (
    <div>
      <h2>{name.common}</h2>
      {flag ? (
        <img src={flag} alt={`Bandera de ${name.common}`} width="100" />
      ) : (
        <p>Bandera no disponible</p>
      )}
      <p><strong>Fronteras:</strong></p>
      {borders.length > 0 ? (
        <ul>
          {borders.map((border, index) => (
            <li key={index}>{border}</li> // Mostrar países fronterizos
          ))}
        </ul>
      ) : (
        <p>No hay fronteras disponibles.</p>
      )}
      <p><strong>Población:</strong> {population.toLocaleString()}</p>
    </div>
  );
};

export default CountryInfo;
